using System;
using Server;
using Server.Misc;
using Server.Gumps;
using Server.Network;
using Server.Commands;
using Server.Items;
using System.Text;
using Server.Mobiles;
using System.Collections;
using Server.Commands.Generic;
using Server.Regions;

namespace Server.Gumps
{
	public class SpeechGump : Gump
	{
        public SpeechGump( Mobile from, string sTitle, string sText ) : base( 50, 50 )
        {
			string color = "#d5a496";

            this.Closable=true;
			this.Disposable=true;
			this.Dragable=true;
			this.Resizable=false;

			AddPage(0);

			AddImage(0, 2, 9543, Server.Misc.PlayerSettings.GetGumpHue( from ));
			AddHtml( 12, 15, 341, 20, @"<BODY><BASEFONT Color=" + color + ">" + sTitle + "</BASEFONT></BODY>", (bool)false, (bool)false);
			AddHtml( 12, 50, 380, 253, @"<BODY><BASEFONT Color=" + color + ">" + sText + "</BASEFONT></BODY>", (bool)false, (bool)true);
			AddButton(367, 12, 4017, 4017, 0, GumpButtonType.Reply, 0);
        }

		public override void OnResponse( NetState sender, RelayInfo info )
		{
			Mobile from = sender.Mobile;
			from.SendSound( 0x4A ); 
		}
    }
}

namespace Server.Misc
{
  class SpeechFunctions
  {
		public static string SpeechText( Mobile m, Mobile from, string sConversation )
		{
			string sYourName = from.Name;
			string sMyName = m.Name;

			Server.Gumps.MyChat.speechText ( sConversation, from );

			string sText = "";

			if ( sConversation == "Ranger"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e posso ensiná-lo sobre como sobreviver na terra. Sobreviver na natureza é frequentemente mais fácil para aqueles que podem viver por sua astúcia e não com a ajuda da magia. Nós, rangers, não precisamos de pergaminhos mágicos ou feitiços de poder. Sabemos quando descansar e sabemos onde descansar. Colocamos nossos acampamentos estrategicamente para descansar em completa segurança. Um aventureiro bem descansado é um aventureiro que retorna para contar suas histórias. Se você deseja seguir esta vida de viver da terra, deve praticar a habilidade de acampar. Com um saco de dormir e uma simples fogueira, você pode descansar com segurança durante a noite. Você também pode fazer uso de pequenas barracas. Essas barracas são úteis quando você se encontra em perigo imediato. Se você conseguir montar sua barraca rápido o suficiente, pode permanecer seguro por alguns minutos enquanto se prepara para os perigos lá fora. Esses tipos de barracas só podem ser montados ao ar livre, então não tente usá-las dentro de masmorras e tumbas.<br><br>Barracas de acampamento são o que mantêm os melhores rangers em movimento por longos períodos de tempo. Pode levar semanas antes que um ranger retorne a um assentamento, pois barracas de acampamento podem fornecer quase tudo que um ranger precisa. Para o ranger habilidoso, uma barraca de acampamento pode fornecer comida e água, juntamente com segurança ilimitada para descanso. Aqueles especialistas em acampar podem construir suas barracas de acampamento e ter ainda mais confortos. O suficiente para atrair outros aventureiros que podem fornecer alguns de seus produtos como comida, bebida e equipamento de sobrevivência. Essas barracas de acampamento também permitem que você organize seus itens do seu banco. A parte mais legal das barracas de acampamento é que você pode usá-las enquanto está no subterrâneo. Isso permite uma maior exploração de masmorras, pois você pode armazenar com segurança seu tesouro encontrado e não se sobrecarregar. Às vezes vendo barracas pequenas e barracas de acampamento, mas você pode encontrá-las mais facilmente com comerciantes.<br><br>Você pode usar armadilhas para ajudar a caçar criaturas. Às vezes você pode comprar as ferramentas de armadilha simples, mas os funileiros geralmente podem fazer as melhores ferramentas de armadilha, pois podem usar metais que causam mais dano do que o ferro simples. Simplesmente use as ferramentas e uma armadilha será colocada a seus pés. Embora você possa evitar sua própria armadilha, outros podem não conseguir. Quanto maior sua habilidade de remover armadilhas, mais eficaz sua armadilha será. Se você tiver ferramentas de armadilha feitas de metais mais preciosos, seria ainda mais eficaz.<br><br>Viagens seguras."; }
			else if ( sConversation == "Shipwright"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e posso ensiná-lo sobre navegação e como navegar pelos altos mares. Quando você finalmente puder comprar seu próprio navio, receberá uma escritura a partir da qual você pode ter seu navio construído. Navios só podem ser colocados na água e deve haver algum tipo de doca nas proximidades. Quando você colocar seu navio em doca seca, também deve estar perto de uma doca. Quando em doca seca, você receberá uma pequena representação daquele navio para carregar consigo para guardar em segurança. Leve isso para qualquer fonte de água ao ar livre e clique duas vezes nela, onde você será solicitado a colocá-la na água. Uma chave será colocada em sua mochila e em seu banco para trancar e destrancar o vaso. Se você tiver magia de recordar, pode usar a chave para viajar magicamente para seu barco, não importa onde ele esteja. Agora, se você tem sua própria casa, pode obter uma 'lanterna de doca' de mim para colocar em sua casa. Isso também pressupõe que sua casa seja construída perto de um corpo d'água. A 'lanterna de doca' deve ser protegida em sua casa. Isso permite que você lance, ou coloque em doca seca, um navio perto dessa casa. Você pode até definir a segurança na lanterna para que apenas certas pessoas em quem você confia possam fazer uso de sua luz.<br><br>Você pode pintar seu navio da cor que quiser, mas lembre-se de que todo o navio terá a cor que você pintá-lo. Use qualquer método que você usaria para tingir itens em seu navio que estão em sua mochila. Quando você colocar o navio na água, ele terá essa cor. Já vi alguns navios de aparência horrível por causa disso, mas isso permite que você tenha um navio fantasmagórico ou até mesmo um navio escuro e maligno nos altos mares. Não importa se na água ou em sua posse, o navio manterá a cor na qual foi pintado.<br><br>Quanto à navegação do navio, você deve estar no convés do seu barco para pilotá-lo. Clique duas vezes no timoneiro e uma janela aparecerá que permitirá que você navegue pelo navio. Você pode colocar esta janela sobre seu mini-mapa para uma melhor navegação. Você notará diamantes vermelhos nas diferentes direções. Selecione-os para mover o barco nessa direção. Não se esqueça de levantar a âncora primeiro. Isso pode ser feito no placar de madeira. Lá você pode soltar ou levantar a âncora. Você pode renomear seu navio para o que quiser. Por último, você pode definir a velocidade do seu navio para velocidade total ou mover-se um passo de cada vez. No placar, há três setas triangulares. Há uma à esquerda, uma à direita e uma na parte inferior. Selecionar as da esquerda ou direita virará seu navio nessa direção. Selecionar a inferior fará com que seu vaso dê a volta. Há uma bandeira vermelha presa ao placar. Selecionar isso parará seu navio. Há uma aba cinza à direita do placar. Selecionar isso alternará esta janela de um tamanho de mapa para outro. Se você deseja colocar seu navio em doca seca, certifique-se de que o porão esteja vazio, bem como o convés. Enquanto estiver em terra, clique duas vezes no timoneiro e ele perguntará se você deseja colocar o navio em doca seca.<br><br>Se você tiver uma carta náutica de um cartógrafo, pode usá-la para traçar um curso. Depois de traçar o curso no mapa, entregue-o ao timoneiro. Assim que ele exclamar 'um mapa!', ele o devolverá a você e você pode simplesmente dizer 'iniciar'. O timoneiro seguirá o curso que você definiu na carta náutica.<br><br>Existem diferentes tamanhos e estilos de navio. O tipo que você adquire importa pouco. Os navios maiores permitem que você tenha mais passageiros, e o tamanho do porão aumenta muito quanto maior o navio. Alguns gostam de navegar pelo mundo e exibir sua riqueza. Outros meramente buscam aventura nos altos mares. Alguns buscam apenas relaxar e pescar. Barcos abandonados durarão apenas cerca de 30 nasceres do sol antes que o casco apodreça e ele afunde no mar, então não a deixe sozinha por tanto tempo sem colocá-la em doca seca. Seja cuidadoso por lá, não importa o motivo. Piratas geralmente navegam por aí e não fazem prisioneiros. Que o vento esteja a seu favor."; }
			else if ( sConversation == "Banker"){ sText = "Bem-vindo ao nosso banco, " + sYourName +". Meu nome é " + sMyName + ", e se você precisar converter alguma moeda, posso ajudá-lo com isso. Normalmente, os comerciantes não lidam com nada além de moedas de ouro. Aventureiros frequentemente encontram moedas dos velhos tempos. Moedas como cobre e prata não são muito incomuns quando se está procurando. Normalmente pegamos o cobre e a prata, derretemos e vendemos aos ferreiros locais. Trocaremos 1 moeda de ouro para cada 5 moedas de prata que você nos trouxer. Também trocaremos 1 moeda de ouro para cada 10 moedas de cobre que você nos trouxer. Isso pode parecer injusto, mas você não encontrará outra maneira de se livrar dessas moedas. Simplesmente nos dê uma pilha de moedas e nós as converteremos para você. Você também pode colocar as moedas em sua caixa bancária, clicar duas vezes na pilha e elas serão convertidas também. Se você encontrar pilhas de joias, também as converteremos para você, pois o joalheiro local frequentemente vem para negociar por elas. Você saberá quando encontrá-las, pois elas são do tamanho da maioria das moedas, mas variam em cor. Não confunda joias com gemas como diamantes e safiras. Joias são encontradas em pilhas como moedas. Para cada joia que você trouxer aqui, daremos 2 de ouro por cada.<br><br>Também lidamos com alguns tipos de pedras e cristais que você pode encontrar. Pepitas de ouro são algo que podemos transformar em moedas para você, e não queremos dizer minério de ouro. Pepitas de ouro têm um brilho cintilante. Você também pode encontrar pedras preciosas, que são diferentes de joias ou pedras comuns como diamantes. Daremos 2 moedas de ouro para cada uma de suas pedras preciosas, pois usamos essa moeda com alguns outros reinos distantes. Cristais são outra forma de moeda que às vezes usamos com seres de outros planos de existência, então estamos muito interessados neles, pois são muito difíceis de encontrar. Alguns afirmam que mineraram por eles nas Minas de Morinia, mas ninguém parece retornar para contar a verdade de tais histórias. Eles são de cor vermelha cintilante e daremos com prazer 5 de ouro para cada cristal que você encontrar.<br><br>Isso pode parecer um pouco estranho, mas há moedas que estamos vendo que são de um verde brilhante. Aventureiros afirmam que as encontram em algum castelo antigo que caiu do céu séculos atrás. Se você encontrar essas moedas, daremos com prazer 3 de ouro para cada uma que encontrar. Elas são bastante raras, então as chances de você encontrá-las são improváveis. Mais uma coisa, você precisa de um cofre para sua casa? Vendemos cofres que permitem que você acesse sua conta bancária de sua casa. Eles custam uma grande quantia de ouro, mas nós os vendemos mesmo assim."; }
			else if ( sConversation == "Weaponsmith"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e se você precisar que sua arma seja reparada, pode me contratar para fazê-lo. Devo me desculpar, porém, pois não conserto armas de longo alcance. Talvez um arqueiro possa ajudá-lo com quaisquer armas de longo alcance que você precise reparar."; }
			else if ( sConversation == "Variety"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e se você encontrar algo incomum em suas aventuras, posso querer dar uma olhada. Posso pagar bem se você encontrar itens raros como vasos, pinturas, estátuas, estandartes ou tapetes. Há outros itens raros que você pode descobrir, como reagentes incomuns que um alquimista pode querer, armaduras ou escudos incomuns que armeiros podem buscar, armas decorativas incomuns que um armeiro teria interesse, ou livros raros que escribas adorariam ler. Você poderia talvez encontrar pergaminhos antigos de feitiços estranhos que você nunca poderia decifrar, mas um mago pagaria muito por eles. Tábuas de pedra de textos há muito esquecidos podem ser procuradas por sábios. Tecido especial poderia ser negociado com um alfaiate ou couro incrível seria interessante para um coureiro. Encontrar pilhas de peles estranhas seria algo que um negociante de peles desejaria, onde bardos frequentemente adoram adicionar instrumentos antigos à sua coleção. Até encontrar um barril de hidromel finamente envelhecido seria muito aceito pela taverna local. Sempre que você encontrar esses itens, pode mantê-los para si mesmo ou entregá-los a um de nós, comerciantes locais, que desejariam o item. Você receberá ouro comensurável com o valor e a raridade do item. Aqueles que são comerciantes habilidosos ou negociantes de armas geralmente recebem mais ouro do que o aventureiro médio.";
				if ( Server.Items.MuseumBook.IsEnabled() )
				{
					sText = sText + "<br><br>Também busco um aventureiro experiente para catalogar um número particular de antiguidades para meu museu que ainda não foram comprovadas existir. Há 60 dessas antiguidades no total e elas podem estar por aí em algum lugar. Se você estiver interessado em me ajudar, e tiver descoberto as nove terras conhecidas, então me entregue 500 moedas de ouro para que eu possa escrever uma cópia do livro que você precisará levar com você. Traga o livro de volta para mim quando encontrar todas as antiguidades e eu o recompensarei com " + MuseumBook.QuestValue() + " de ouro. Também trocaria ouro pelas antiguidades que você encontrar, pois cada uma tem um valor atribuído. Apenas me dê o item se você escolher e eu lhe darei o ouro por ele.";
				}
			}
			else if ( sConversation == "Thief"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", mas isso é conhecido apenas por alguns. Se você encontrar um baú com uma fechadura teimosa, pode me contratar para removê-la. Se você está mais interessado nas habilidades que tenho, talvez o livro A Arte do Saque de Tumbas possa ajudá-lo em seu caminho. Aqueles habilidosos em roubar podem tirar muitas coisas das masmorras que mais ninguém consegue. Você pode achar que vale a pena.<br><br>Ensinar a si mesmo como se esgueirar pode ser um grande teste de habilidade, desde que você atinja 30.0 em Furtividade. Para se tornar mais proficiente, você precisa tentar ser furtivo com armaduras mais pesadas à medida que melhora na habilidade. Aqui estão alguns exemplos de itens que você pode usar para a faixa de habilidade correspondente que você está tentando alcançar atualmente:<br><br>30.0-57.4:<br>   - Gorjal de Couro<br>   - Luvas de Couro<br><br>57.4-65.0:<br>   - Luvas de Cota de Anéis<br>   - Mangas de Couro<br>   - Gorjal de Couro<br><br>65.0-84.5:<br>   - Luvas de Cota de Anéis<br>   - Mangas de Cota de Anéis<br>   - Gorjal de Couro<br><br>84.5-95.0:<br>   - Luvas de Cota de Anéis<br>   - Perneiras de Cota de Anéis<br>   - Túnica de Couro<br>   - Mangas de Couro Reforçado<br>   - Gorjal de Couro Reforçado<br><br>95.0-100:<br>   - Perneiras de Couro<br>   - Túnica de Cota de Malha<br>   - Gorjal de Couro Reforçado<br>   - Capuz de Couro<br><br>100-120:<br>   - Túnica de Cota de Malha<br>   - Mangas de Cota de Anéis<br>   - Gorjal de Armadura de Placas<br><br>Você pode usar armadilhas para ajudar a pegar viajantes desavisados desprevenidos. Às vezes você pode comprar as ferramentas de armadilha simples, mas os funileiros geralmente podem fazer as melhores ferramentas de armadilha, pois podem usar metais que causam mais dano do que o ferro simples. Basta usar as ferramentas e uma armadilha será colocada a seus pés. Embora você possa evitar sua própria armadilha, outros podem não conseguir. Quanto maior sua habilidade em remover armadilhas, mais eficaz sua armadilha será. Se você tiver ferramentas de armadilha feitas de metais mais preciosos, seria ainda mais eficaz.<br><br>Agora deixe-me falar sobre as cidades. Nós não seríamos uma guilda de ladrões sem aliviar as bolsas dos cidadãos locais. Este é um empreendimento complicado, mas os mercadores carregam itens em seus bolsos e moedas em seus cofres. Você só precisa bisbilhotar o mercador ou o cofre para ver se há algo para roubar. Se o mercador tiver algum item, você pode tentar roubá-los. Se o cofre tiver ouro, então use sua habilidade de Roubo no próprio cofre. Tente não ser notado porque você pode ter os guardas chamados para você. Você pode roubar moedas e outras coisas de outras criaturas ficando próximo a elas e atacando-as, onde você pode automaticamente roubar tais itens ao dar o ataque.<br><br>Por último, se você quiser fazer alguns trabalhos obscuros para nossa clientela mais proeminente, então sussurre para meu mestre da guilda e ele lhe dará um trabalho para realizar. Estes não são limitados a membros da guilda, pois qualquer ladrão capaz pode fazê-los. Você receberá uma mensagem sobre o que precisa roubar e onde pode ser encontrado. Estas são tarefas incomuns porque ninguém quer ser testemunhado trocando tais itens, então você sempre receberá instruções sobre para qual cidade você precisa levar o item. Uma vez entregue, sua recompensa estará esperando junto com outro trabalho que você pode fazer se quiser.<br><br>Cuidado por aí. Lembre-se de que você pode conseguir um ataque furtivo em seus inimigos se você atacá-los enquanto estiver escondido. Executar tais ataques testará suas habilidades em Furtividade e Esconder-se para aumentar o dano do ataque. Inimigos poderosos seriam melhor lidados se você os cumprimentasse com tal golpe."; }
			else if ( sConversation == "Tailor"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e se você não gosta da aparência de sua capa ou manto, você poderia me contratar para alterá-la para você. Algumas dessas vestes podem parecer incomuns em relação ao que vendo normalmente, mas posso fazê-las parecerem normais para você. Se você conseguir um capuz ou capucho, às vezes a cor deles pode ser alterada. Basta usá-lo quando estiver em sua cabeça. Você pode selecionar algo que você esteja equipado, como um manto, e isso mudará sua cor para combinar. Isso permite que você defina facilmente sua vestimenta com um manto com capuz ou uma capa."; }
			else if ( sConversation == "Scribe"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e eu posso decifrar quase qualquer coisa. Há muitos conjuradores de feitiços que buscam poder místico, mas a única maneira de obter tais coisas é explorar as masmorras esquecidas da terra. Se você encontrar quaisquer pergaminhos incomuns, pode me contratar para lhe dizer o que eles são. Isso pode tornar seu livro de feitiços mais completo. Também tenha em mente que você pode ostensivamente segurar certos livros de feitiços no seu slot de bugigangas. Isso vem como um benefício se o seu livro de feitiços tiver propriedades que vão além dos limites normais.<br><br>Além disso, se você encontrar quaisquer pergaminhos estranhos, pode me contratar e eu os identificarei e decifrarei para você. Esses pergaminhos geralmente têm pistas ou enigmas que levam a muitas coisas estranhas e ricas... mas metade do tempo eles são simplesmente falsos ou enganosos. Eu não sou o único que pode descobrir o que os pergaminhos dizem. Um aventureiro muito inteligente também poderia descobrir. Os pergaminhos podem ser codificados de forma simples (20 de int), codificados de forma especializada (30 de int), codificados de forma hábil (40 de int), codificados de forma inteligente (50 de int), codificados de forma traiçoeira (60 de int), codificados de forma genial (70 de int) ou codificados de forma diabólica (80 de int). Se a informação nele é verdadeira ou falsa, eu não poderia lhe dizer... mas talvez uma cigana possa lançar alguma luz sobre isso para você.<br><br>Se você quer algumas funções de bibliotecário, e está praticando inscrição, há muitos livros e estantes nas áreas perigosas da terra que você pode querer investigar. Você precisará de um kit de bibliotecário, que você poderia talvez comprar ou pedir a um soprador de vidro para criar para você. Alguns simplesmente os encontraram. Esses kits de bibliotecário o ajudarão a procurar nos livros e estantes em masmorras, por exemplo. Simplesmente use um kit de bibliotecário em uma estante ou pilha de livros para investigá-la mais a fundo. Kits de bibliotecário se desgastam, então certifique-se de levar o suficiente com você se for em uma longa jornada de pesquisa. Procurar por esses livros e estantes pode render pergaminhos em branco, bem como pergaminhos de natureza mágica. Você também pode talvez encontrar livros raros.<br><br>Se você é um mago ou necromante, e deseja realizar pesquisas para aumentar seu conhecimento nas formas antigas de magia, posso vender-lhe uma bolsa de pesquisa para guardar todos os seus materiais de pesquisa. Você não só pode pesquisar as formas antigas de magia, mas também pode pesquisar feitiços amplamente conhecidos que você pode ter dificuldade em encontrar o feitiço original. A pesquisa requer que se procure pelo mundo tomos de informações de magos antigos para construir os feitiços. Você também deve se tornar muito proficiente em inscrição para pesquisar e escrever a nova magia sobre a qual você aprende. Se você quiser prosseguir com esta pesquisa, então me dê 500 moedas de ouro e eu lhe darei uma bolsa especificamente para você."; }
			else if ( sConversation == "Mapmaker"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e talvez você esteja em busca de um tesouro perdido? Se você encontrar um mapa do tesouro que não consegue decifrar, você poderia me contratar para ajudá-lo. Claro, quanto mais tesouro estiver supostamente nele, mais você me pagará para que eu lhe diga o que o mapa diz. Você também pode praticar cartografia por conta própria, desenhando mapas você mesmo. Se você ficar bom o suficiente, pode decodificar seus próprios mapas do tesouro. Mapas do tesouro mostram uma parte do mundo onde o tesouro pode ser encontrado, bem como as coordenadas de sua localização, então traga um sextante com você. Quando você chegar ao local, use uma pá comum no próprio mapa e você começará a desenterrar o tesouro. No entanto, tome cuidado, pois abrir o baú pode resultar em criaturas percebendo e elas certamente tentarão impedi-lo de reivindicar sua descoberta. Por fim, posso desenhar um mapa mundi para ajudá-lo a viajar mais facilmente. Se você me der 1.000 ouros, eu começarei."; }
			else if ( sConversation == "Mage"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e você pode me contratar para recarregar quaisquer itens mágicos que você possa ter. Tais itens têm um feitiço mágico imbuído nele e permitem que você conjure tal feitiço. Só posso recarregar tais itens se eles tiverem uma quantidade mínima e máxima de usos, pois aqueles sem uma quantidade máxima nunca podem ser recarregados por qualquer mago. Normalmente exijo 100 por círculo de feitiço da varinha, o que aumentará as cargas em 5.<br><br>Além disso, se você está curioso sobre as artes mágicas... tenho alguns conselhos para você. O feitiço 'criar comida' não só cria comida, mas também cria uma garrafa mística de água. Certifique-se de comer e beber durante sua jornada. O feitiço 'remover armadilha' não deve ser esquecido. Ele faz mais do que simplesmente remover armadilhas de recipientes. Ele também criará um orbe mágico que vai para sua mochila. Esta varinha verificará passivamente se há armadilhas nos pisos e paredes... junto com aquelas colocadas em recipientes. O feitiço 'telecinese' permite que você pegue um objeto que está fora de seu alcance e o coloque em sua mochila. Ele não funciona em itens empilhados ou itens em outro recipiente. Algumas masmorras têm armadilhas e baús escondidos dentro delas. Embora o feitiço 'revelar' tenha sido feito para apenas encontrar criaturas que se escondem, ele também encontrará armadilhas e recipientes de tesouro ocultos.<br><br>Conjurar feitiços pode ser um processo tedioso, mas não precisa ser. Há um segredo que poucos magos conhecem. Você pode ter até 4 barras de feitiços personalizadas com as quais pode conjurar. Essas barras de feitiços mantêm seus feitiços favoritos ao alcance dos dedos e permitem que você os conjure mais rapidamente. Para aprender os comandos para acessar esses segredos, você pode encontrá-los usando o botão 'Ajuda' no paperdoll.<BR><BR>Um aviso sobre a prática de outras formas de magia: elementalismo e magia não podem ser concedidos ao mesmo conjurador. Conhecer ambas as formas fará com que qualquer um dos feitiços falhe ao tentar conjurar. Isso inclui ter itens que aprimorem esses tipos de habilidades. Um aspirante a conjurador deve escolher um caminho e avançar em direção a esse objetivo. Você pode buscar o elementalismo ou pode aprender sobre a magia mais diversificada e poderosa. Se você achar que está perdendo o interesse em uma dessas escolas de magia e quer rapidamente buscar a outra, então procure pela Caverna do Feiticeiro em Sosaria ou pela Caverna do Conjurador em Lodoria. Eles têm cristais descobertos séculos atrás, que podem ajudá-lo a esquecer o que você aprendeu em uma área da magia para que você possa aprender outra."; }
			else if ( sConversation == "Monk"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e sou um praticante de longa data das artes exóticas do bushido, ninjutsu, shinobi e do caminho do monge. Posso ensiná-lo em muitas dessas habilidades e frequentemente vendo tomos e pergaminhos que permitirão que você pratique essas habilidades sozinho. Um estilo de vida exótico não é para todos, mas se você escolher este caminho, pode querer explorar a terra em busca de tesouros orientais (em suas opções de Ajuda, você pode definir seu Estilo de Jogo para Oriental). Quando você faz isso, muitos dos itens que você encontrar serão de natureza exótica, desde armaduras, armas e outras relíquias.<br><br>Tanto o bushido quanto o ninjutsu são os mais fáceis de dominar, mas aqueles que buscam a busca dos místicos ou monges precisam encontrar os santuários adequados e meditar neles para aprender as várias habilidades que podem ser usadas. Se você adquirir um Tomo do Monge, ele explicará tudo isso para você. A outra busca rara é o caminho do shinobi. Essas habilidades aprimoram um ninja fornecendo mais habilidades que eles podem usar, como disfarces, correr tão rápido quanto um cavalo ou destrancar baús de tesouro. Só é necessário estar buscando o caminho do ninja e um pergaminho de shinobi para empreender esse objetivo.<br><br>Se você simplesmente quiser comprar alguns itens exóticos, vendo muitos para ninjas ou samurais. Você também é livre para usar nosso dojo para praticar ou descansar."; }
			else if ( sConversation == "Necromancer"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e conjurar feitiços pode ser um processo tedioso, mas não precisa ser. Há um segredo que poucos necromantes conhecem. Você pode ter até 2 barras de feitiços personalizadas com as quais pode conjurar. Essas barras de feitiços mantêm seus feitiços favoritos ao alcance dos dedos e permitem que você os conjure mais rapidamente. Para aprender os comandos para acessar esses segredos, você pode encontrá-los usando o botão 'Ajuda' no paperdoll.<br><br>Além disso, se você tem estômago para isso, pode pegar uma pá de cemitério e ir para o cemitério. As pessoas são enterradas com todos os tipos de objetos de valor, e não há necessidade de um homem morto tê-los. Essas ações trabalham suas habilidades forenses, o que pode torná-lo um bom agente funerário um dia.<br><br>Antes que eu me esqueça, o texto antigo que você encontrará está incorreto. O feitiço 'exorcismo' não faz realmente o que o livro afirma. Ele realmente é usado contra demônios e os mortos. Se você for poderoso o suficiente, pode enviá-los de volta de onde vieram.<BR><BR>Um aviso sobre a prática de outras formas de magia: elementalismo e necromancia não podem ser concedidos ao mesmo conjurador. Conhecer ambas as formas fará com que qualquer um dos feitiços falhe ao tentar conjurar. Isso inclui ter itens que aprimorem esses tipos de habilidades. Um aspirante a conjurador deve escolher um caminho e avançar em direção a esse objetivo. Você pode buscar o elementalismo ou pode aprender sobre a magia mortal da necromancia. Se você achar que está perdendo o interesse em uma dessas escolas de magia e quer rapidamente buscar a outra, então procure pela Caverna do Feiticeiro em Sosaria ou pela Caverna do Conjurador em Lodoria. Eles têm cristais descobertos séculos atrás, que podem ajudá-lo a esquecer o que você aprendeu em uma área da magia para que você possa aprender outra."; }
			else if ( sConversation == "Elementalism")
			{
				string lowreg = "Se você conseguir encontrar itens mágicos que tenham uma qualidade de reagente inferior, então a stamina necessária para os feitiços será reduzida proporcionalmente por esse valor. ";
				if ( MyServerSettings.LowerReg() < 1 )
					lowreg = "";

				sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e pratico a magia dos elementos. Diferente de outras formas de magia, o elementalismo depende tanto do intelecto quanto da condição física do conjurador. Nenhum reagente é necessário, mas conjurar esses feitiços requer um uso igual de mana e stamina. " + lowreg + "Não há habilidades suplementares necessárias para elementalistas, como os magos com sua psicologia ou os necromantes com seu espiritualismo. Nós temos uma guilda da qual você pode participar em nosso Liceu, que está escondido nas montanhas da Guarda do Diabo, com um feitiço de ilusão. Para chegar lá, você só precisa conjurar o feitiço Santuário. Este feitiço não pode ser usado em qualquer lugar, mas à medida que você aprende mais sobre elementalismo, você será capaz de conjurá-lo em lugares como masmorras para chegar rapidamente à segurança.<BR><BR>Se você conseguir visitar o Liceu, há quatro santuários para os elementos Terra, Ar, Água e Fogo. Cada elementalista só pode se concentrar em um elemento por vez, e eles podem mudar seu foco sempre que quiserem. Para fazer isso, simplesmente suba no santuário de sua escolha. Se você quiser mudar sua roupa para combinar com seu elemento focado, simplesmente diga a palavra 'culara'. Mudar o foco não afeta sua biblioteca de feitiços. Isso significa que se você tem 10 feitiços em seu Livro de Feitiços Elemental da Terra, e você muda seu foco para Magia Elemental da Água, então seu Livro de Feitiços Elemental da Água alterado terá os mesmos 10 tipos de feitiços nele.<BR><BR>A única maneira de escrever feitiços elementais em pergaminhos é escrevendo as palavras mágicas com sangue de demônio, e conjurar feitiços pode ser um processo tedioso, mas não precisa ser. Há um segredo que poucos elementalistas conhecem. Você pode ter até 2 barras de feitiços personalizadas com as quais pode conjurar. Essas barras de feitiços mantêm seus feitiços favoritos ao alcance dos dedos e permitem que você os conjure mais rapidamente. Para aprender os comandos para acessar esses segredos, você pode encontrá-los usando o botão 'Ajuda' no paperdoll.<BR><BR>Diz-se que nossa magia foi forjada pelos Titãs dos Elementos, e muitos elementalistas tentam encontrar o caminho para o Submundo para procurá-los. O que eles desejam são os livros de feitiços dos Titãs, pois são muito poderosos. A maioria dos magos e elementalistas têm sua conjuração de feitiços frequentemente falhando no Submundo. Os elementalistas acreditam que equipar um dos livros de feitiços do Titã lhes permitiria explorar o reino sombrio sem esse impedimento. Se verdade ou falsidade, só tentando.<BR><BR>Um aviso sobre a prática de outras formas de magia: magia e necromancia não podem ser concedidas ao mesmo elementalista. Conhecer essas outras formas de magia fará com que qualquer um dos feitiços falhe ao tentar conjurar. Isso inclui ter itens que aprimorem esses tipos de habilidades. Um aspirante a conjurador deve escolher um caminho e avançar em direção a esse objetivo. Você pode buscar o elementalismo ou pode aprender sobre magia e necromancia. O conhecimento do Elementalismo até afeta as forças das varinhas mágicas ou da magia rúnica. A busca pela pesquisa mágica também é algo que os elementalistas não podem alcançar. Se você achar que está perdendo o interesse em uma dessas escolas de magia e quer rapidamente buscar a outra, então procure pela Caverna do Feiticeiro em Sosaria ou pela Caverna do Conjurador em Lodoria. Eles têm cristais descobertos séculos atrás, que podem ajudá-lo a esquecer o que você aprendeu em uma área da magia para que você possa aprender outra.<BR><BR>Por fim, a magia elemental é muito sensível às forças que cercam o conjurador. Quanto mais armadura você estiver usando, mais provável é que seus feitiços falhem. Ou você evita usar armadura ou encontra armadura adequada para conjuradores, que talvez tenham uma qualidade de armadura de mago. Escudos com forças de canalização de feitiços também ajudam. A armadura de metal é a mais obstrutiva a esse respeito, onde a armadura de madeira é menos prejudicial. A armadura de couro é a menos impactante, mas encontrar roupas de qualidade é o curso desejado.";
			}
			else if ( sConversation == "LeatherWorker"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e eu poderia consertar sua equipamento de couro para você, se desejar me contratar. Posso consertar armaduras de couro, máscaras de urso, máscaras de veado, e até mesmo luvas de pugilista e de arremesso."; }
			else if ( sConversation == "Herbalist"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e você já encontrou reagentes incomuns em suas viagens? Se sim, você poderia me contratar para lhe dizer o que eles são. Você não pode usá-los em poções, ou feitiços mágicos, sem saber primeiro o que você tem."; }
			else if ( sConversation == "Furtrader"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e se você tiver uma máscara de pele de urso ou veado, pode me contratar para consertar quaisquer rasgos ou rasgões que ela possa ter. Você quer mantê-la em boa forma, não é?"; }
			else if ( sConversation == "Bowyer"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e eu sou especializado em consertar arcos e bestas desgastados. Se você precisar dos meus serviços a esse respeito, sinta-se à vontade para me contratar.<br><br>Também posso mudar o estilo de uma aljava para você, se você simplesmente me entregá-la."; }
			else if ( sConversation == "Alchemist"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e talvez você tenha encontrado alguns líquidos estranhos? Se sim, você poderia me contratar para identificá-los para você. É preciso uma pessoa rara para saber dizer o que todos esses líquidos são, você pode se surpreender."; }
			else if ( sConversation == "Blacksmith"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e talvez você esteja aqui porque sua armadura ou espada quebrou? Você veio ao lugar certo. Posso consertar quase qualquer arma, exceto arcos e bestas. Também posso consertar quase qualquer armadura de metal que você tenha."; }
			else if ( sConversation == "Armorer"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e sou muito habilidoso em reparar armaduras de metal. Se você tiver alguma que precise dos meus serviços, sinta-se à vontade para me contratar. Vou deixá-la quase como nova. Você não quer ficar na frente de um dragão com a armadura quebrada, quer?"; }
			else if ( sConversation == "Knight"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", o Cavaleiro, e talvez você esteja interessado em buscar o caminho da cavalaria? É um caminho difícil, mas se você permanecer fiel... será mais poderoso do que o mais poderoso dos bárbaros.<br><br>A magia de cavaleiro pode ser um processo tedioso para invocar, mas não precisa ser. Há um segredo que poucos cavaleiros conhecem. Você pode ter até 2 barras de feitiços personalizadas com as quais pode conjurar. Essas barras de feitiços mantêm seus feitiços favoritos ao alcance dos dedos e permitem que você os conjure mais rapidamente. Para aprender os comandos para acessar esses segredos, você pode encontrá-los usando o botão 'Ajuda' no paperdoll.<br><br>Também lembre-se de que somos capazes de remover maldições. Isto não se refere apenas às maldições no corpo, mas também em itens. Se você acabar tendo um de seus itens amaldiçoado, ou seus tomos de conhecimento trancados em uma caixa amaldiçoada, você pode remover a maldição por conta própria... se tiver a habilidade para invocar a magia de 'remover maldição'"; }
			else if ( sConversation == "Provisioner")
			{
				string mercrate = "<br><br>Às vezes vendemos caixas de comerciante para sua casa, se você for um artesão. São caixas onde a Guilda dos Mercadores virá à sua casa uma vez por dia e pegará o que estiver na caixa em troca de ouro. Isso pode realmente ajudar aqueles que querem montar uma loja e ganhar algum ouro com os itens que fabricam.";

				if ( !MySettings.S_MerchantCrates )
					mercrate = "";

				sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e talvez você precise de algum equipamento antes de sair? Há muitas coisas que carrego que podem ajudá-lo. Lanternas e tochas podem iluminar seu caminho nas masmorras mais escuras. Atenção, esses itens eventualmente se apagarão e você terá que acender outro. Muitos aventureiros caem em armadilhas colocadas nesses lugares escuros. Se você quiser ser mais cauteloso, vendo varas de 10 pés. Elas permitem que você bata em recipientes, pisos e paredes para procurar armadilhas. Isso funciona passivamente, então você só precisa de uma em sua mochila e você verificará automaticamente tais perigos. Quase todas as armadilhas são invisíveis a olho nu, então cuidado. Algumas são mais óbvias, pois pode haver uma face de pedra em uma parede que dispara fogo ou cogumelos que podem explodir com esporos nocivos. Com um saco de dormir e uma simples fogueira, você pode descansar com segurança durante a noite. Você também pode fazer uso de barracas pequenas. Essas barracas são úteis quando você se encontra em perigo imediato. Se você conseguir montar sua barraca rápido o suficiente, pode permanecer seguro por alguns minutos enquanto se prepara para os perigos lá fora. Esses tipos de barraca só podem ser montados ao ar livre, então não tente usá-los dentro de masmorras e tumbas.<br><br>Barracas de acampamento são o que mantêm os melhores aventureiros em ação por longos períodos. Pode levar semanas até que alguém retorne a um assentamento, pois barracas de acampamento podem fornecer quase tudo o que um aventureiro precisa. Para aqueles habilidosos em acampamento, uma barraca de acampamento pode fornecer comida e água, juntamente com segurança ilimitada para descanso. Aqueles especialistas em acampamento podem construir suas barracas de acampamento e ter ainda mais confortos. O suficiente para atrair outros aventureiros que podem fornecer a você alguns de seus produtos, como comida, bebida e equipamento de sobrevivência. Essas barracas de acampamento também permitem que você organize seus itens do banco. A melhor parte das barracas de acampamento é que você pode usá-las enquanto está no subsolo. Isso permite uma maior exploração de masmorras, pois você pode armazenar com segurança o tesouro encontrado e não se sobrecarregar." + mercrate + "<br><br>Se você tiver algum item incomum, às vezes posso lhe dizer o que você pode obter por ele e de quem. Esses são itens estranhos que geralmente são decorativos por natureza, e não algo que você seria capaz de usar. Pode ser armadura, obra de arte, estandartes, livros, tecido, carpetes, moedas, peles, gemas, lápides, instrumentos, joias, couro, orbes, pinturas, reagentes, tapetes, pergaminhos, estátuas, tabuletas, vasos ou armas. Se eu não puder lhe dizer o valor disso, então você terá que descobrir o que fazer com isso por conta própria. Se eu determinar que tem valor, entregue o item ao comerciante que eu mencionar e ele lhe dará algum ouro por ele. Apenas entregue-o a eles ou guarde-o para decorar sua casa.<br><br>Viagens seguras.";
			}
			else if ( sConversation == "Architect"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e talvez você esteja interessado em possuir terras e construir uma casa? Só posso fornecer um contrato de construção, mas cabe a você encontrar a área onde deseja construir. Você deve encontrar uma área semiplana sem grandes obstruções e suficientemente limpa para o tamanho da casa que deseja construir. ";

			if ( MySettings.S_AllowCustomHomes ){ sText = sText + "Você pode construir uma casa padrão ou um lote que você pode personalizar. "; }

			if ( MySettings.S_AllowHouseDyes ){ sText = sText + "O que torna as casas padrão únicas é que você pode pintá-las antes de construí-las. Simplesmente use algo para tingir o contrato com uma cor de sua escolha. Quando você construir a casa, ela estará totalmente nessa cor específica. Claro, já vi algumas casas de aparência horrível que foram pintadas, mas isso permite que você construa estruturas como uma torre escura ou um castelho vermelho-sangue. Você poderia até construir uma estrutura que pareça talhada em gelo graças a este recurso. Embora a estrutura terá a cor que você pintar, as portas e a placa da casa não terão. "; }

			sText = sText + "<br><br>Você pode encontrar algumas masmorras abandonadas por toda a terra. Nós as limpamos de pequenas pragas e as colocamos à venda. Se você encontrar uma à venda, e tiver o ouro, você pode comprá-la.<br><br>Ao longo dos anos, alguns magos faleceram por acidentes ou velhice. Eles possuíam grandes castelos no céu que também colocamos à venda. Eles são difíceis de encontrar, pois você deve encontrar a corda mística que leva a esses castelos no céu. Eles não são tão caros quanto as masmorras, e também não fornecem todo o espaço e benefícios que as masmorras têm... mas geralmente são maiores do que os castelos que você poderia construir no chão.<br><br>Decorar sua casa pode ser uma tarefa e tanto quando você encontrou muitas relíquias interessantes que deseja exibir. Se você conseguir um conjunto de 'ferramentas do proprietário', esse trabalho ficará muito mais fácil. Essas ferramentas permitem que você levante ou abaixe itens travados em sua casa. Você também pode movê-los para o norte, sul, leste ou oeste. Você pode rapidamente garantir ou desproteger coisas com esta ferramenta, travar itens ou colocar um barril de lixo. Alguns itens podem ser virados em duas direções diferentes, e essas ferramentas podem fazer isso por você. Se você conseguir um item/escritura que tenha uma direção em seu nome (como leste ou sul), coloque esse item/escritura no chão de sua casa e use a opção 'virar escritura' para obter o item/escritura em uma direção diferente. Lembre-se de que a maioria desses itens específicos são 'escrituras', mas há algumas coisas como tapetes ou troféus que se parecem com o item que será colocado na casa. Se você colocar um item do tipo escritura em sua casa, você pode removê-lo (retorná-lo à escritura) cortando o item com um machado. Você pode se livrar do barril de lixo de forma similar.";

				if ( Server.Items.BasementDoor.IsEnabled() )
				{ 
					sText = sText + "<br><br>Se você quiser ter acesso ao porão público da sua casa, você simplesmente precisa comprar um alçapão de porão e segurá-lo no chão da sua casa. Esta área pública é acessada por muitos lugares na terra, seja a partir das casas das pessoas ou de certas lojas como funileiros, ferreiros ou alfaiates. Os porões têm áreas onde as pessoas podem fabricar, mas somente se entrarem na área do porão a partir de uma loja de comerciante. Ele fornece uma área comum para as pessoas se reunirem e artesãos venderem seus produtos e talvez consertarem seus itens. Os porões são áreas seguras onde você pode relaxar e se encontrar com outros.";
				}
				if ( Server.Items.MovingBox.IsEnabled() )
				{ 
					sText = sText + "<br><br>Se você precisar se mudar de sua casa atual para outra, o trabalho pode ser bastante envolvido. Vendo algumas caixas de mudança que você poderia usar. Elas contêm muitos, muitos itens e a caixa é fácil de carregar mesmo quando totalmente cheia. As caixas, no entanto, só podem ser abertas por aquele a quem eu as vendo e só podem ser abertas dentro da casa de alguém. Se você tentar travá-las ou segurá-las na casa, a contagem de itens irá para o armazenamento da sua casa. Também é melhor encher a caixa enquanto ela estiver no chão da sua casa e não na sua mochila. Caso contrário, você pode estar tentando colocar recipientes, com mais itens do que sua mochila pode suportar, dentro da caixa enquanto ela está na sua mochila e isso não funcionará bem. Porém, uma vez que a caixa estiver cheia, você pode então colocá-la em sua mochila sem problemas. Você também pode abrir a caixa quando estiver no banco, se precisar mover seus pertences para uma casa recém-colocada. No entanto, tenha cuidado, porque a caixa pode ser roubada por outros ou simplesmente perdida. Então seus pertences mundanos estarão perdidos para sempre.";
				}
				if ( MySettings.S_LawnsAllowed )
				{ 
					sText = sText + "<br><br>Para proprietários de casas que querem adicionar um pouco de caráter ao perímetro externo de suas casas, eles podem comprar algumas ferramentas de jardim de mim. Essas ferramentas permitem que você coloque itens como árvores, rochas, água, decorações de quintal e vários tipos de terreno fora dos limites de sua casa. Depois de comprá-las, simplesmente leve-as para casa e use-as enquanto estiver em seu terreno. Você terá opções sobre o que deseja construir. Cada opção custa uma quantia específica de ouro, onde quaisquer fundos serão retirados de sua caixa bancária. Você pode aprender detalhes adicionais sobre essas ferramentas quando as usar em sua casa.";
				}
				if ( MySettings.S_ShantysAllowed )
				{ 
					sText = sText + "<br><br>Se você é um proprietário que quer reformar sua casa, então você pode comprar algumas ferramentas de remodelação de mim. Essas ferramentas permitem que você coloque itens como paredes, portas, escadas ou outras coisas decorativas dentro dela. Depois de comprá-las, simplesmente leve-as para casa e use-as enquanto estiver em sua casa. Você terá opções sobre o que deseja construir. Cada opção custa uma quantia específica de ouro, onde quaisquer fundos serão retirados de sua caixa bancária. Você pode aprender detalhes adicionais sobre essas ferramentas quando as usar em sua casa.";
				}
			}
			else if ( sConversation == "Guard"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e sou um guarda em patrulha aqui. Não há muitas leis na terra, mas as poucas que existem... devem ser seguidas. Não tente roubar de ninguém. Se pego, você será marcado como criminoso por 2 minutos. Durante este tempo, você pode ser morto por outros sem repercussões para eles. Eles também podem pegar livremente coisas que pertencem a você. Mais importante, nós o mataremos à vista. Outras coisas que o tornarão um criminoso são atacar outra pessoa inocente.<br><br>Assassinato é o mais vil dos crimes. O arauto da cidade saberá de todos os procurados por assassinato na terra. Matar outra pessoa inocente o marcará como um assassino. Cada violação de assassinato só será esquecida por todos se você estiver dentro do reino por 40 horas do mundo real. Quer você seja um assassino ou um criminoso, o povo da cidade não terá tratos com tal escória. É melhor ficar do lado certo da lei e da ordem.<br><br>Também colocamos recompensas em criminosos que assolam nossas terras. Se você me trouxer a cabeça de um pirata, ou um dos muitos tipos de ladrões, você receberá ouro por seus esforços. Também há assassinos, caçadores ou ninjas por aí. Temos um preço por esse bando assassino também. Normalmente é um ato ofensivo cortar a cabeça de um cadáver, mas fazer tal coisa com essa escória não trará má vontade em relação a você."; }
			else if ( sConversation == "Healer"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ". Provavelmente há algumas coisas que você precisa saber sobre nosso grupo. Nós o curaremos de graça, mas apenas de vez em quando. Nós veremos que você precisa de ajuda e auxiliaremos. Morte, é outra questão. Invocar espíritos dos mortos é uma tarefa exaustiva, como qualquer bom mago atestará. Se seu espírito encontrar o caminho até aqui, exigiremos uma doação à nossa ordem para ajudá-lo. É uma pequena contribuição para retornar à terra dos vivos, com certeza. Os deuses o ressuscitarão por conta própria, mas o custo para seu corpo será grande se você permitir que eles façam isso.<br><br>Há também santuários ao redor das terras. Esses lugares não só permitem que alguém dízime seu ouro, mas também ressuscitará a alma. Esses santuários exigirão a mesma quantia de ouro ou dízimo que nós, mas os santuários podem estar mais perto do seu cadáver do que nós.<br><br>Ouvimos rumores de poções de ressurreição, e até mesmo uma mistura que se poderia fazer com a fabricação de bruxaria, que poderia trazer a alma de volta à vida. Não vemos tais coisas, então é difícil ter certeza. Talvez você tenha mais sorte nesses assuntos.<br><br>Se você é cauteloso com o uso de magia para cura, ataduras podem ser o melhor item que você pode carregar consigo. Alguns ficaram tão bons em curar com ataduras, que são conhecidos por curar alguém de veneno ou trazê-los de volta dos mortos. Para usar ataduras mais facilmente, você tem algumas opções. Você pode usar as macros internas para enfaixar a si mesmo ou a outros. Outra opção são os comandos '[bandself' ou '[bandother'. Qualquer um funciona bem e poderia possivelmente salvar sua vida.<br><br>Não importa como alguém chegue ao seu fim, seu espírito será guiado para o curandeiro ou santuário mais próximo se buscar ajuda na ressurreição de sua alma.<br><br>Uma vez que alguém é trazido de volta dos mortos, eles frequentemente procuram o lugar onde encontraram seu fim. Isso pode ser uma questão simples quando alguém chega ao fim em um corredor ou caverna, mas o ermo é uma questão diferente, pois a paisagem pode parecer a mesma de um lugar para outro. Se você precisar de ajuda para encontrar seus restos mortais, simplesmente use o comando '[corpse' e você será levado até ele... se estiver perto o suficiente."; }
			else if ( sConversation == "Druid"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + " o Druida. Sou o protetor de todas as coisas na floresta. Todas as plantas e animais servem a um propósito no mundo e o equilíbrio disso deve ser mantido. Se seu companheiro animal sofrer um destino horrível, você pode ser capaz de trazer a alma dele para mim e eu o ressuscitaria para você. Não cobro por tais serviços, pois é meramente meu dever manter esse equilíbrio.<br><br>Você também pode estar interessado no herbalismo druídico para fazer poções únicas. Há um livro chamado 'Herbalismo Druídico', que explicará como você pode explorar esta magia oculta da natureza."; }
			else if ( sConversation == "Cook"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", o especialista culinário por aqui. Para se tornar um chef, simplesmente cozinhe! Para muitos dos preparativos você precisa estar perto de uma fonte de calor, isso geralmente é um forno, mas em alguns casos, como churrasco, uma fogueira ou forja é suficiente. Abaixo estão alguns exemplos de fontes de calor para cozinhar:<br><br>Fogueira<br>Lareira<br>Forja<br>Suporte de aquecimento<br>Forno<br><br>Algumas ferramentas de cozinha comuns necessárias são um rolo de massa, frigideira ou um peneirador de farinha."; }
			else if ( sConversation == "Sage"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + " o Sábio. Se você tem uma varinha mágica desconhecida, ou talvez algum artefato estranho que não consegue identificar... então talvez eu possa ajudá-lo com isso. Exijo apenas cerca de 500 de ouro para identificar varinhas, e 5.000 de ouro para identificar artefatos. Claro, você poderia praticar essa habilidade sozinho e pode não precisar mais dos meus serviços.<br><br>Se você quer algumas funções de bibliotecário, e está praticando inscrição, há muitos livros e estantes nas áreas perigosas da terra que você pode querer investigar. Você precisará de um kit de bibliotecário, que você poderia talvez comprar ou pedir a um soprador de vidro para criar para você. Alguns simplesmente os encontraram. Esses kits de bibliotecário o ajudarão a procurar nos livros e estantes em masmorras, por exemplo. Simplesmente use um kit de bibliotecário em uma estante ou pilha de livros para investigá-la mais a fundo. Kits de bibliotecário se desgastam, então certifique-se de levar o suficiente com você se for em uma longa jornada de pesquisa. Procurar por esses livros e estantes pode render pergaminhos em branco, bem como pergaminhos de natureza mágica. Você também pode talvez encontrar livros raros.<br><br>Além disso, um pouco de conselho antes de você partir. O conhecimento está em toda parte e deve ser procurado. Você pode encontrar livros em masmorras que contêm lendas e pistas. Você pode conversar com alguém em uma cidade ou vila que pode fornecer informações valiosas. Não deixe pedra sobre pedra. Se você encontrar um livro, veja se suas páginas contêm algo de valor. Eu mesmo vendo muitos tomos que podem ajudá-lo através das provações deste mundo.<br><br>Além disso, se você encontrar quaisquer pergaminhos estranhos, pode me contratar para identificá-los e decifrá-los para você. Esses pergaminhos geralmente têm pistas ou enigmas que levam a muitas coisas estranhas e ricas... mas metade do tempo eles são simplesmente falsos ou enganosos. Eu não sou o único que pode descobrir o que os pergaminhos dizem. Um aventureiro muito inteligente também poderia descobrir. Os pergaminhos podem ser codificados de forma simples (20 de int), codificados de forma especializada (30 de int), codificados de forma hábil (40 de int), codificados de forma inteligente (50 de int), codificados de forma traiçoeira (60 de int), codificados de forma genial (70 de int) ou codificados de forma diabólica (80 de int). Se a informação nele é verdadeira ou falsa, eu não poderia lhe dizer... mas talvez uma cigana possa lançar alguma luz sobre isso para você.<br><br>Se você é um mago ou necromante, e deseja realizar pesquisas para aumentar seu conhecimento nas formas antigas de magia, posso vender-lhe uma bolsa de pesquisa para guardar todos os seus materiais de pesquisa. Você não só pode pesquisar as formas antigas de magia, mas também pode pesquisar feitiços amplamente conhecidos que você pode ter dificuldade em encontrar o feitiço original. A pesquisa requer que se procure pelo mundo tomos de informações de magos antigos para construir os feitiços. Você também deve se tornar muito proficiente em inscrição para pesquisar e escrever a nova magia sobre a qual você aprende. Se você quiser prosseguir com esta pesquisa, então me dê 500 moedas de ouro e eu lhe darei uma bolsa especificamente para você.<br><br>Se você quer uma grande missão para desenterrar um artefato, pode buscar meu conselho em sua jornada. Meu conselho não é barato, onde você pode gastar 10.000 de ouro pelo melhor conselho que posso dar. Para começar sua missão, entregue-me ouro suficiente pelo meu conselho. Eu lhe darei uma enciclopédia de artefatos a partir da qual você pode procurar as primeiras pistas sobre o paradeiro de seu artefato.<br><br>Nunca somos capazes de lhe dar informações absolutamente precisas sobre a localização de um artefato, mas os deuses permitindo, você encontrará o que está procurando. Uma vez que você receber sua enciclopédia, abra-a e escolha um artefato de suas muitas páginas. Se você não tem certeza de qual artefato procura, simplesmente olhe meus produtos à venda. No final do meu inventário, você verá réplicas de pesquisa desses artefatos com preço zero de ouro. Você pode passar o mouse sobre esses artefatos para ver o que eles podem oferecer, mas você não pode comprá-los. Artefatos como livros, aljavas e instrumentos serão mostrados com algumas qualidades comuns e aleatórias, onde encontrar o artefato real terá propriedades um pouco diferentes. Os itens restantes têm qualidades definidas, bem como um número de Pontos de Encantamento que você pode gastar para tornar o artefato mais personalizado para você. Quando você encontrar esses artefatos, clique uma vez neles e selecione a opção Encantar para gastar os pontos nos atributos adicionais que desejar. Depois de selecionar um artefato do livro, você arrancará a página apropriada e jogará fora o resto do livro. Esta página lhe dará sua primeira pista sobre onde procurar. As áreas onde o artefato pode estar podem abranger muitas terras ou mundos diferentes, onde alguns você pode nunca ter estado ou ouvido falar. Você receberá as coordenadas do lugar que procura, então certifique-se de ter um sextante com você.<br><br>Ao longo da história, muitas pessoas mantiveram esses artefatos armazenados em blocos de pedra trabalhada. Essas pedras trabalhadas são frequentemente decoradas com um símbolo na superfície, onde uma arca de metal repousa e o item pode estar dentro. Alguns caçadores de tesouros encontram as arcas vazias, percebendo que as lendas eram falsas. Quanto melhor o nível de conhecimento do livro de onde você arrancou a página, melhor a chance de você encontrar o artefato. No mínimo, você pode encontrar uma grande soma de ouro para cobrir algumas de suas despesas nesta jornada. Alguns podem fornecer uma nova pista sobre onde o artefato está, e você atualizará suas anotações quando essas pistas forem encontradas. A busca mais decepcionante pode render um artefato falso. Estes acabam sendo itens inúteis que simplesmente se parecem com o artefato que você estava procurando. <br><br>Essas missões são bastante complexas e você só pode participar de uma dessas missões por vez. Se você não terminou uma missão e tenta buscar um sábio para outra, descobrirá que a página de sua missão anterior terá desaparecido. Ela certamente teria sido perdida em algum lugar. Se você terminar uma missão, seja com sucesso ou fracasso, não terei nenhum novo conselho para você por um bom tempo, então você terá que esperar até então para começar uma nova missão. Então, boa sorte caçador de tesouros, e que os deuses o auxiliem em sua jornada."; }
			else if ( sConversation == "Bard"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + " e sou um grande músico. Bardos não apenas entretêm outros na taverna local, nós também temos habilidades místicas com nossa música. Podemos fazer com que outros se tornem agressivos uns com os outros, ou podemos tocar música suave para acalmá-los. Também podemos tocar música tão irritante, que nossos oponentes frequentemente vacilarão na batalha. Uma das formas mais raras de performances bárdicas estão geralmente no livro de canções de um bardo. Embora eu possa ter um livro vazio à venda, as canções são algo de uma arte perdida. Alguns bardos encontraram as notas e letras profundamente dentro de masmorras, mas essas histórias podem ser simplesmente rumores. Às vezes tenho uma ou duas canções à venda, mas é isso. Acredito que dezesseis canções foram escritas ao longo dos séculos, pelo menos aquelas com efeitos mágicos.<br><br>Canções bárdicas podem ser usadas assim como magos conjuram feitiços. Você pode invocar a magia de seu livro, ou usar as barras de canções conforme explicado usando o botão 'Ajuda' no paperdoll. Diferente de outros tipos de magia, canções bárdicas têm um comando que você pode digitar para cada canção. Este comando é algo similar a '[KnightsMinne', e você pode ver qual comando vai com qual canção em seu livro de canções bárdicas. A eficácia de muitas dessas canções é dependente do conhecimento de música do bardo. Os melhores resultados dessas canções são realizados por aqueles que são bons em musicalidade, provocação, discordância e pacificação. Você também precisa de um instrumento para tocar essas canções. O livro de canções bárdicas permitirá que você defina seu instrumento de escolha. Se você perder o instrumento, simplesmente abra o livro e atribua um novo instrumento. Se você tiver um instrumento com propriedades de exterminador, estes terão efeitos maiores naqueles inimigos. Cada vez que você toca uma dessas canções, os usos do seu instrumento diminuirão em um, então esteja atento à condição do seu instrumento.<br><br>Se você criar abelhas, você provavelmente poderia fazer alguma cera que pode usar em instrumentos. Isso os ajuda a durar mais. Às vezes também vendo este tipo de cera.<br><br>Alguns instrumentos têm poderes místicos. Se você for um músico suficientemente habilidoso, pode equipar esses instrumentos ou livros de canções onde você normalmente colocaria bugigangas. Isso pode permitir que você aproveite os efeitos mágicos que os itens podem ter. Se você decidir fazer um instrumento com sua habilidade de carpintaria, lembre-se de que quanto melhor a madeira e a habilidade, mais usos você obterá do instrumento."; }
			else if ( sConversation == "Tanner"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e eu poderia consertar sua equipamento de couro para você, se desejar me contratar. Posso consertar armaduras de couro, máscaras de urso, máscaras de veado, e até mesmo luvas de pugilista e de arremesso. Às vezes também vendo kits de taxidermia. Se você for bom em alfaiataria, compre um desses kits que permitirá que você monte certas criaturas como troféus. Uma vez que você pegar o kit de taxidermia, veja os vários troféus que você pode fazer. Você então precisa carregar o kit de taxidermia com você quando for caçar essas criaturas. Se você não tiver o kit de taxidermia com você, não obterá o item necessário para fazer o troféu. Boa sorte na caça."; }
			else if ( sConversation == "Tavern"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e você veio ao lugar certo se não quer viajar sozinho. Ajudantes são seguidores que podem se juntar a você em aventuras para que você não tenha que atravessar as masmorras perigosas sozinho. Você pode comprar os serviços de um ajudante de barmen, donos de taverna ou estalajadeiros. Há três tipos diferentes de ajudantes que você pode ter... um lutador, arqueiro ou mago. Esses ajudantes usam um sistema similar para animais domesticados, com algumas exceções. Primeiro, você pode curar seus ajudantes com sua habilidade de cura. Segundo, você não pode transferir um ajudante ativo para outro jogador. Terceiro, você não pode estabular seus ajudantes. Por último, você não pode ter um vínculo com seus ajudantes. Cada 'item de ajudante' representa o tipo de ajudante que você tem (elmos para lutadores, bestas para arqueiros, ou bolas de cristal para magos). Embora você não possa transferir seu ajudante, você pode dar o 'item de ajudante' para outra pessoa, que então terá posse do ajudante. Nesse sentido, se alguém conseguir pegar seu 'item de ajudante' de você, o ajudante é então deles.<br><br>Você deve estar em uma área como uma estalagem, taverna ou casa para chamar seu ajudante. Uma vez que você os chamar, eles tomarão posse do 'item de ajudante' e o manterão até que uma das seguintes situações ocorra... eles forem mortos, você os libertar, ou eles ficarem irritados com a falta de tesouro sendo encontrado. Para cada 5 de ouro que você lhes der, eles viajarão com você por 1 minuto. Isso equivale a 300 de ouro por hora, onde o máximo que eles aceitarão de você é o suficiente para 6 horas de aventura. Você pode pagar seu ajudante de algumas maneiras diferentes. Você pode dar a eles muitos tipos de tesouro, como moedas, gemas ou itens raros como pagamento. Itens raros são aqueles itens únicos que você pode encontrar e dar aos comerciantes nas cidades por um alto preço. Cada vez que você os pagar, receberá uma mensagem indicando quantos minutos eles viajarão com você. Quando eles tiverem cerca de 5 minutos restantes, começarão a expressar sua irritação pela falta de tesouro. Este é um aviso para encontrar algum tesouro rapidamente, ou seu ajudante irá embora. Se seu ajudante partir, o 'item de ajudante' aparecerá em sua mochila. Da próxima vez que você chamar seu ajudante, certifique-se de ter algo para dar a eles para que viajem com você. Um ajudante sempre se lembra de quanto tesouro você lhes deu. Isso significa que se um ajudante tiver cerca de 4 horas restantes de viagem, e você 'libertá-lo', ele se lembrará que tem 4 horas de viagem quando você o chamar novamente. Lembre-se de que este 'tempo de aventura' não conta quando você está em uma área como uma taverna, casa, estalagem, banco ou barraca de acampamento.<br><br>Cada ajudante terá um nome, título e roupas únicos. Se você não gostar da aparência do seu ajudante, simplesmente dê o 'item de ajudante' a um estalajadeiro, dono de taverna ou barman e eles trocarão o ajudante por outro. Quando você fizer isso, o novo ajudante reterá quaisquer ataduras e tempo restante para aventuras que o ajudante anterior tinha... pois os pagamentos e ataduras são transferidos para o novo ajudante. Como mencionado anteriormente, você não estabula ajudantes. Em vez disso, você os 'liberta' e seu 'item de ajudante' aparecerá em sua mochila e você pode chamar o ajudante mais tarde. Você pode libertar ajudantes em qualquer lugar onde estiver. Se um ajudante for morto, o 'item de ajudante' aparecerá em sua mochila. O nome do 'item de ajudante' indicará que o ajudante está morto. Você terá que procurar um curandeiro e 'contratá-lo' para ressuscitar seu ajudante. Quando você 'contratar' um curandeiro para fazer isso, custará uma quantia de ouro indicada no item... e você deve selecionar o 'item de ajudante' quando o cursor de mira aparecer. O indicador 'morto' desaparecerá e você pode então retornar a uma área como uma estalagem, taverna, banco ou casa e chamar seu ajudante novamente.<br><br>Se você montar uma criatura ou magicamente aumentar sua velocidade de viagem, seu ajudante montará uma monta logo depois para que possa acompanhá-lo. Ajudantes são tão capazes quanto aventureiros quanto você é. Seu nível de habilidade é um valor médio do total de suas habilidades. Seus atributos são uma distribuição do total de seus atributos não magicamente melhorados. Então, basicamente, quanto melhor você for... melhor seus ajudantes serão. Esses ajudantes só o ajudam em suas batalhas. Eles não arrombam fechaduras ou removem armadilhas. Isso cabe a você gerenciar. Você pode dar a eles ataduras e eles as usarão conforme precisarem para curar seu veneno ou curar seus ferimentos. Se você quiser saber quantas ataduras eles têm left em sua mochila, simplesmente diga 'report' e eles lhe dirão. Você pode dar a eles poções e eles as beberão... devolvendo-lhe uma garrafa vazia. As poções que eles podem usar são de cura, antídoto, rejuvenescimento, refrescante e mana. Você só pode levar dois ajudantes com você a qualquer momento."; }
			else if ( sConversation == "Gypsy"){ sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + ", e temos a reputação de ter visões do passado ou futuro. Se você tiver um pergaminho decifrado, e estiver se perguntando se as palavras escritas falam a verdade, eu poderia 'revelar' se é verdade ou mentira. Pediria pagamento em ouro dependendo de como o pergaminho foi escrito. Há pergaminhos codificados de forma simples (100 de ouro), especializada (200 de ouro), hábil (300 de ouro), inteligente (400 de ouro), traiçoeira (500 de ouro), genial (600 de ouro) ou diabólica (700 de ouro) que eu poderia ler para você.<br><br>Se você talvez encontrou uma nota simples, e está se perguntando se as palavras nela são verdadeiras, olharei feliz no tarô e verei se o pergaminho foi escrito com verdade ou falsidade (100 de ouro).<br><br>Se você tem uma lenda que precisa ser verificada, isso requer grande concentração e esforço. Se você quiser que eu busque a validade de uma lenda, elas custam de acordo com a probabilidade de sua história. Então você pode ter uma lenda altamente improvável (4000 de ouro), improvável (3500 de ouro), um pouco improvável (3000 de ouro), um pouco confiável (2500 de ouro), confiável (2000 de ouro) ou altamente confiável (1500 de ouro) que eu poderia examinar para você."; }
			else if ( sConversation == "MadGodPriest"){ sText = "Então, " + sYourName + "... você aprendeu o verdadeiro nome do Deus Louco. Seus discípulos crescem a cada dia. Em breve seremos uma voz poderosa para invocá-lo e ele nos libertará desta prisão que Mangar teceu. Aqueles que verdadeiramente adoram Tarjan, o fazem nas profundezas das Catacumbas. Pegue esta chave e busque a iluminação nas trevas abaixo."; }
			else if ( sConversation == "NecroGreeter"){ sText = "Salve, " + sYourName + "... esta é a ilha de Drácula, o Mestre Vampiro. Ele fez deste lugar um santuário para sua espécie, mas tem paciência para aqueles que estudam as artes necróticas. Qualquer necromante que atingiu um nível de habilidade de aprendiz, um agente funerário que atingiu um nível de habilidade adepto, cavaleiros da morte vis igualmente habilidosos, ou aqueles estranhos cultistas Syth são capazes de vagar pela ilha sem medo de ataque dos minions de Drácula. Eles também são capazes de construir santuários para si mesmos aqui também. Atenção, ninguém deve adentrar no castelo de Drácula.<br><br>Para aqueles necromantes, agentes funerários, cavaleiros da morte ou cultistas Syth que desejam construir uma fortaleza aqui, há muitos lugares para fazê-lo. A vila de Ravendark está próxima, mas a maioria tranca suas portas apenas para aqueles que podem viver nesta ilha, pois não confiam em outros que não entendem nada do que fazem.<br><br>A matança dos minions do Mestre não é uma preocupação para ele, e muitos necromantes, agentes funerários, cavaleiros da morte ou cultistas Syth frequentemente o fazem para ajudar em seus objetivos de poder.<br><br>Para outros viajantes, cuidado. A ilha é um lugar perigoso para atravessar."; }
			else if ( sConversation == "Kylearan"){ sText = "Mangar me prendeu em minha própria torre, liberando seus minions em meus corredores. Éramos amigos uma vez, Mangar e eu. Agora o poder o corrompeu a ponto de companheiros não servirem mais a um propósito.<br><br>Pegue esta chave de ébano. Ela não funcionará na porta da torre de Mangar, mas ele disse que funcionaria em sua porta secreta sob as ruas de Skara Brae. Também quero que você tenha este pequeno baú que tem uma relíquia dentro. Escolha uma para que possa ajudá-lo em sua missão. Vá agora, e que você derrote Mangar e seu vazio maligno."; }
			else if ( sConversation == "Assassin"){ sText = "Saudações, " + sYourName +". Você conseguiu encontrar nossa pequena mas secreta guilda de assassinos. Se você acha que tem o espírito para se juntar às nossas fileiras, sou eu com quem você falaria. Embora eu lidere a organização da guilda, Xardok é o alto governante de nossa guilda. Ele vive em uma pequena ilha em Sosaria, e você pode procurá-lo se quiser realizar quaisquer deveres para a guilda. No entanto, vendo alguns produtos que podem ajudar um assassino em sua busca por eliminar outros. Se você se meter em problemas com a lei por assassinato, você poderia esperar 40 horas de tempo de jogo do mundo real para ver se os guardas esquecem o crime, mas qualquer assassinato cometido além disso exigirá 40 horas de tempo de jogo do mundo real para garantir que os guardas esqueçam um único assassinato.";
				if ( MySettings.S_Bribery >= 1000 )
				{
					sText = sText + " Você também poderia me contratar por " + MySettings.S_Bribery + " de ouro para subornar os guardas para esquecerem um único assassinato que você possa ter cometido. Membros da guilda pagam apenas metade desse valor e não posso convencer os guardas a esquecerem foragidos. Se você não tiver ouro suficiente em sua mochila, posso simplesmente retirá-lo de sua caixa bancária.";
				}
			}
			else if ( sConversation == "Xardok"){ sText = "Bem-vindo à minha ilha, " + sYourName +". Alguns chamam este lugar de refúgio para os vilões, mas considero-o meramente um lugar para relaxar sem as preocupações da lei. Do mais nobre dos cavaleiros, ao mais assassino dos patifes... meu castelo acolhe qualquer um em seus salões. Há até um ladrão em minha torre sul, e eles têm um baú de metal que pode acessar a caixa bancária de alguém. Venha aqui para praticar, vender mercadorias ou comprar suprimentos... você pode até descansar aqui durante a noite.<br><br>Agora, você pode ter ouvido rumores de que este é o lar da Guilda dos Assassinos. Isso pode, ou não, ser... verdade. A Guilda dos Assassinos é um grupo de pessoas que faz coisas que outros não querem sujar as mãos. As pessoas vão até eles para ter alguém em particular... morto. Qualquer um pode se juntar oficialmente à guilda, eles só precisam procurar um mestre da guilda de assassinos. Alguns acreditam que eles se estabelecem entre os ladrões em sua guilda, secretamente procurando por novos membros. Apenas os membros de má índole da guilda são bem-vindos ao grupo quando um trabalho de assassino é procurado, então alguém com bom carma geralmente é mantido afastado de tais tarefas. Os membros recebem alvos que devem procurar e matar. Cada tarefa deve ser concluída para obter outra. Se você falhar em uma tarefa, o líder da guilda não concede outra, a menos que seja dada uma expiação. Quanto mais famoso um assassino, maior a chance de conseguir uma vítima de alto preço. Claro, quanto mais ouro por um alvo, geralmente significa quão difícil o alvo pode ser de lidar. Aqueles que completam tarefas para a guilda, frequentemente perdem carma, pois a sociedade acha que o que fazem é desprezível... mas isso é algo com que um bom assassino não se preocupa.<br><br>Para ter um alvo designado a você, deve-se simplesmente perguntar ao líder da guilda se ele deseja 'contratá-lo'. Eles não o enviariam para uma terra onde você nunca esteve, mas podem enviá-lo para qualquer cidade ou masmorra em terras que você já viajou. Se você não sabe a localização de um lugar em particular, isso não é preocupação da guilda. É melhor você começar sua exploração de tais áreas. Quaisquer outros detalhes da vítima podem ser lidos no registro de missões (digitando '[quests'). Quando tal tarefa for concluída, normalmente se diria ao líder da guilda que está 'pronto'.<br><br>Se alguém for incumbido de matar um cidadão de uma vila, sabe-se que a guilda paga as pessoas certas para ajudar outros a esquecerem o assassinato do assassino. Claro, tais trabalhos são difíceis, pois o assassino deve escapar da vila para coletar sua recompensa e ser absolvido do crime do qual foi 'injustamente' acusado. Ao realizar essas tarefas, é de suma importância que apenas a vítima pretendida seja assassinada. Qualquer testemunha do ato deve ser rapidamente fugida, pois a guilda não pode fazer uma vila esquecer muitos assassinatos. A maneira mais fácil de eliminar um indivíduo problemático é pegá-lo sozinho, sem os olhos dos guardas em você. Se tal coisa não for possível, alguns dizem que o método mais eficaz é envenenar alguma comida ou bebida. Veneno maior funciona melhor para essas situações. Uma vez que você tenha alguma comida ou bebida contaminada, dê-a à sua vítima para ingerir. Embora muitos possam dizer que a comida e a bebida estão contaminadas, os que estão normalmente marcados parecem tolos às vezes. Certifique-se de que pode se esconder imediatamente, antes que os próximos saibam o que ocorreu. Certifique-se de que pode então escapar sorrateiramente da área antes de ser pego. Não espere que seu alvo sofra os efeitos do veneno. Apenas saia de lá. Se você usou veneno maior ou mortal, é muito provável que eles pereçam a seu devido tempo. Se você não for capaz de se mover com grande furtividade, ou carecer de magia para escapar, então ser um assassino pode não ser para você.<br><br>Mas... no final... tudo isso é apenas lenda e boato."; }
			else if ( sConversation == "XardokFail"){ sText = "O que você está esperando? Você recebeu sua tarefa, agora cumpra-a com diligência! Nós não escolhemos uma vítima para você que seja muito difícil... nós nos certificamos disso. Se você acha que esta vítima está além da sua capacidade, lembrarei que a guilda pouco se importa com o que você pensa. Se você deseja se livrar deste contrato, então deve pagar a recompensa oferecida como sua expiação pelo fracasso. Então, seja qual for a recompensa, você deve colocar o total na caixa de expiação... se desejar rastejar para longe do que pedimos a você."; }
			else if ( sConversation == "ScrapMetal"){ sText = "Este barril é para sucata de metal enferrujado. Se você encontrar algum item de metal enferrujado, coloque-o neste barril e você receberá 5 de ouro por peso de pedra de metal enferrujado. Os metalúrgicos removem as impurezas e fundem-no novamente em barras de ferro. Quaisquer membros da Guilda dos Ferreiros receberão 10 de ouro por peso de pedra de metal enferrujado."; }
			else if ( sConversation == "Aquarium"){ sText = "Você pode ver muitos tipos diferentes de peixes neste tanque. Normalmente, pescadores especialistas pegam um peixe verdadeiramente incomum e os colocam nestes tanques... pois o proprietário deste lugar pagará bem por eles. Eles são difíceis de pegar, mas quanto melhor o pescador... mais provável será que ele pegue um peixe que renderá um bom preço. Quaisquer membros da Guilda dos Marinheiros receberão o dobro da quantia de ouro pelos peixes que pegarem."; }
			else if ( sConversation == "DeathKnight"){ sText = "Se você está em um caminho de caos, estabelecido pela Mão Ensanguentada, então você deve incutir medo nas mentes dos homens que ousam tentar extinguir o fogo das trevas. Se você alcançou a reputação de desprezível e atingiu o status de cavaleiro da morte jornaleiro, então entre no pentagrama e fale o nome da Mão Ensanguentada e você se transformará nos pesadelos de que os homens fogem e as crianças perecem com o mero pensamento.<br><br>Se você atingiu o status de cavaleiro da morte grão-mestre, e suas ações foram desprezíveis o suficiente, posso invocar um cavalo do terror para você. Tudo que exijo são 10.000 de ouro de você como tributo.<br><br>Por último, se você quer ser presenteado com o dom das asas demoníacas, então posso transformar qualquer capa que você tenha em tais coisas. No entanto, elas não permitem que você voe. O ouro que exijo é 35.000, mas seu carma afetará este preço, onde pode custar 20.000 para os mais vis ou 50.000 para os puros de coração. Se você achar que não gosta das asas, devolva-as a mim e eu as transformarei de volta em uma capa sem a exigência de ouro para fazê-lo."; }
			else if ( sConversation == "Patch"){ sText = "À medida que algumas coisas são adicionadas ao mundo, seu cliente pode precisar ser atualizado para acomodar as mudanças. Este quadro de avisos exibirá a última data em que um patch foi preparado, para ajudá-lo a decidir se você já o instalou ou não. Se você baixou o cliente na data exibida ou após, não precisará do patch mostrado. Se precisar baixá-lo, clique uma vez no quadro de avisos e selecione 'Abrir'. Seu navegador da web será aberto para que você possa obter o patch. Quando for baixado, descompacte o conteúdo em seu diretório UO, substituindo os arquivos dentro dele. Certifique-se de que seu cliente esteja fechado antes de fazer isso."; }
			else if ( sConversation == "GodOfLegends"){ sText = "Você está no Salão das Lendas, " + sYourName +". É aqui que os campeões mais notáveis são sepultados. Alguns eram os heróis mais legais, e outros podem ter sido os maiores vilões da terra. Todos compartilham uma característica comum. Eles foram corajosos diante do perigo e mataram muitas criaturas poderosas que percorrem as terras. Eles mataram grandes dragões, demônios poderosos e mortos-vivos formidáveis. Seus nomes são conhecidos por toda a terra e sua história para sempre lembrada.<br><br>Lendas nasceram de sua bravura, e itens foram forjados e empunhados por eles. Da espada de Elric, Stormbringer, ao poderoso cajado de Merlin, esses artefatos lendários às vezes foram perdidos, roubados, escondidos ou encontrados por outros. Embora esses itens estivessem entre os mundanos, esses campeões os usaram em muitas batalhas. Essas vitórias transformaram esses itens em artefatos lendários.<br><br>Não é sua hora de estar entre os poderosos, mas você poderia começar sua jornada com um item de poder forjado em seu nome. Coloquei um livro no pedestal. Se você tem 15.000 pontos de fama, 15.000 pontos de carma, seja para o bem ou para o mal, e 10.000 de ouro para tributo, então você pode selecionar um item do livro. Embora eu crie o item para você, ele aparecerá como algo simplesmente vendido por comerciantes. Enquanto você estiver equipado com o item, ele crescerá em poder à medida que você alcançar a vitória sobre os muitos inimigos temíveis das terras.<br><br>Ao selecionar um item, sua fama e carma serão reduzidos de acordo. Cabe a você reconstruí-los. Você pode buscar tantos artefatos lendários quanto for capaz de alcançar. Eles são muito especiais comparados a itens regulares de aparência comum. Artefatos lendários nunca precisarão ser reparados. Se você encontrar um fim prematuro, você o terá em sua posse quando retornar aos vivos. Certas armadilhas que afetam itens equipados não terão efeitos adversos em artefatos lendários. Criaturas que tentam arruinar artefatos lendários falharão na tentativa. Se você for descuidado com seu artefato e o deixar largado, então o destino falará pelo que pode acontecer com ele. Seu item ganhará níveis enquanto você o equipa e obtém vitória sobre seus adversários. Quando o item ganhar um nível, clique uma vez em seu item e selecione 'Encantar' para dar mais poder ao item. Tenha cuidado ao adicionar poder aos itens, pois você não pode alterar nenhum atributo depois de selecioná-los. Você pode usar tubos de tintura regulares em itens lendários, tornando-os de qualquer cor que escolher. Se você quiser renomear seu artefato, mostre-me que foi presenteado com um entregando-o a mim. Devolverei seu artefato, junto com um ferro de marcar que você pode usar para marcar seu artefato com qualquer nome que escolher."; }
			else if ( sConversation == "Farmer"){ sText = "Saudações, " + sYourName +". Bem-vindo ao meu mercado. Cultivei tudo aqui e você pode encontrar alguma comida que possa ajudá-lo em uma longa jornada. Muitas fazendas cultivam coisas como milho, trigo e nabos. Nós simplesmente os tiramos do jardim quando estão totalmente crescidos. Há também frutas. Elas são encontradas por toda a terra, pois você pode usar um item com lâmina em uma macieira, pessegueiro ou pereira para colher a fruta delas. Se você se encontrar em uma rara ilha tropical, você também pode talvez obter bananas, tâmaras ou cocos da mesma forma. A agricultura pode exigir mais trabalho, mas é mais segura do que tentar dar um soco em um urso pela carne desejada."; }
			else if ( sConversation == "Courier"){ sText = "Saudações, " + sYourName +". Às vezes entrego correio de pessoas proeminentes procurando por um aventureiro resistente para assumir uma tarefa específica. Viajamos de vila em cidade, cidade em vila, procurando por aqueles para quem precisamos entregar. Essas mensagens são geralmente enviadas para uma pessoa específica para encontrar um item raro de um lugar que outros temem ir. Se você acabar recebendo uma mensagem você mesmo, certifique-se de que pode fazer o que é pedido. Se não puder, provavelmente não verá outra mensagem entregue a você tão cedo.<br><br>Você receberá as coordenadas do lugar que procura, então certifique-se de ter um sextante com você e a mensagem enviada por correio. Sem a mensagem enviada por correio, é provável que você nunca encontre o item.<br><br>Os itens procurados serão armazenados em blocos de pedra trabalhada. Essas pedras trabalhadas são frequentemente decoradas com um símbolo na superfície, onde uma arca de metal repousa e o item pode estar dentro. Essas missões são tratadas de maneira 'virtual'. O que isso significa é que quaisquer conquistas são reais, mas quaisquer referências a itens encontrados não são. Se sua mensagem de missão por correio afirmar que você encontrou o item, você não terá o item em sua mochila... mas você o terá 'virtualmente'. A mensagem por correio manterá o controle desse fato para você. Por causa disso, você nunca perderá esse item e ele permanece único para sua mensagem por correio. A mensagem por correio sabe que você o encontrou e o tem. Entregue a mensagem por correio àquele que a enviou para você para receber sua recompensa."; }
			else if ( sConversation == "Painter"){ sText = "Salve, " + sYourName +". Como você pode ver ao seu redor, sou um pintor que fez muitos retratos ao longo dos anos. Eu poderia fazer um para você, pintarei um retrato seu como você está agora. Claro, eu exigiria 5.000 de ouro pelo meu tempo e uma tela que estou vendendo, a propósito. Você ficará feliz com isso, disso tenho certeza. Faço pinturas em vários estilos diferentes. Se eu criar uma de você que você gostaria que fosse diferente, simplesmente me devolva a pintura e eu a substituirei gratuitamente. Mesmo que você tenha um novo título, posso adicioná-lo a uma pintura previamente pintada se você me entregá-la. Afinal, quero meus clientes satisfeitos. Claro, você não precisaria dos meus serviços se fosse bom em trabalhar com cera, mas divago.<br><br>Se você conseguir encontrar alguma pintura rara durante sua viagem, pagarei o dobro do que o colecionador de arte pagaria. Eles sempre gostam de acumular a arte rara, então tenho que dar um pouco a mais se quiser colecionar essas peças raras."; }
			else if ( sConversation == "Trophy"){ sText = "Esta é uma placa de base de madeira para você montar um peixe ou uma cabeça. Essas montagens podem então ser usadas para decorar sua casa. Simplesmente clique uma vez nesta placa e selecione 'Usar'. Você pode então mirar no cadáver de uma criatura ou peixe. Se puder ser montado, você criará o troféu para levar com você. Quaisquer criaturas que você montar serão gravadas com quem matou a criatura e onde foi morta. Você não pode montar a cabeça de todas as criaturas. Abaixo está uma lista de criaturas cujas cabeças você pode montar:<br><br>Antaurs<br>Balrons<br>Ursos<br>Bugbears<br>Cérbero<br>Quimeras<br>Cíclopes<br>Daemons<br>Veado<br>Demônios<br>Diabos<br>Tartarugas Dragão<br>Dragões<br>Drakes<br>Ettins<br>Fiends<br>Golems de Carne<br>Gárgulas<br>Gigantes<br>Goblins<br>Gorilas<br>Grifos<br>Feras do Inferno<br>Hipogrifos<br>Hidras<br>Leões<br>Homens-Lagarto<br>Manticoras<br>Meglassauros<br>Minotauros<br>Pesadelos<br>Ogres<br>Orcs<br>Urso-Coruja<br>Fadas<br>Homens-Rato<br>Stegosaurus<br>Styguanas<br>Pterodátilos<br>Tigres<br>Titãs<br>Ursos Troll<br>Trolls<br>Tiranossauros<br>Unicórnios<br>Morsa<br>Observadores<br>Wyverns<br>Yetis<br>"; }
			else if ( sConversation == "Stuff"){ sText = "Esta é uma base de madeira para você montar uma criatura empalhada. Essas bases podem então ser usadas para decorar sua casa. Simplesmente clique uma vez nesta base e selecione 'Usar'. Você pode então mirar no cadáver de uma criatura. Se puder ser empalhada, você criará o troféu para levar com você. Quaisquer criaturas que você matar serão gravadas com quem matou a criatura e onde foi morta. Você não pode empalhar todas as criaturas. Abaixo está uma lista de criaturas que você pode montar:<br><br>Macacos<br>Ursos<br>Pássaros Gigantes<br>Krakens<br>Raptors<br>Escorpiões<br>Serpentes<br>Lulas<br>Sapos<br>"; }
			else if ( sConversation == "Stuffing"){ sText = "Esta é uma cesta de algodão com a qual você pode empalhar criaturas. Essas criaturas empalhadas podem então ser usadas para decorar sua casa. Simplesmente clique uma vez nesta cesta e selecione 'Usar'. Você pode então mirar no cadáver de uma criatura. Se puder ser empalhada, você criará o troféu para levar com você. Quaisquer criaturas que você matar mostrarão quem matou a criatura e onde foi morta. Você só é capaz de empalhar ursos, leões, serpentes e homens-lagarto. Você também pode usar isso para fazer tapetes da maioria dos tigres."; }
			else if ( sConversation == "Stonecrafter")
			{
				string mercrate = ", ou simplesmente colocá-lo em uma caixa de comerciante para as guildas de comerciantes negociarem";

				if ( !MySettings.S_MerchantCrates )
					mercrate = "";

				sText = "Saudações, " + sYourName +". Meu nome é " + sMyName + " e sou um artesão da pedra. Vendo tomos que podem ensiná-lo a minerar pedra, mas você deve ter obtido um título de grão-mestre em mineração para fazê-lo. Também vendo livros que mostrarão como manipular a pedra em várias estátuas e outras formas. Para ser capaz de fazer isso, no entanto, você precisaria ser um grão-mestre em carpintaria. Há diferentes cores de pedra que você pode encontrar, e elas imitam os tipos de minério que você pode cavar. Qualquer coisa que você criar a partir de pedra terá a cor da pedra da qual foi feita. Você pode usar o que cria, vendê-lo para outros que possa conhecer" + mercrate + ". Você pode mudar a cor da maioria dos itens artesanais com um tubo de tintura para móveis, ou outros meios que você pode descobrir para alterar a cor. Você geralmente pode virar os itens para enfrentar uma direção ou outra com o uso de ferramentas do proprietário. Se você tiver um bloco de pedra maior, que coloca uma estátua ou peça de mobília em sua casa, pode usar um método similar para virá-los. Neste caso, coloque o bloco no chão de sua casa e use a opção para virar uma escritura. A direção mudará no nome e então você poderá construir a estátua ou mobília em sua casa. A maioria dos itens, que não são blocos que você constrói em sua casa, podem ter seus nomes alterados. Clique duas vezes no item e você será solicitado a dar um novo nome. Esta é uma opção útil, pois você pode então nomear estátuas ou lápides que esculpe.";
			}
			else if ( sConversation == "Stake"){ sText = "Você encontrou um malho de madeira e uma estaca. Se você conseguir matar um vampiro, pode clicar uma vez nisto e usá-lo para cravar uma estaca no coração da criatura. Fazer isso lhe dará crédito por tais mortes. Quando você tiver adquirido um valor de pelo menos 250, leve isto ao sacerdote na Britain ou na Cidade de Lodoria e ele lhe dará o ouro que você busca. Se você está procurando se tornar um sacerdote, então precisará de alguma habilidade em Cura e Espiritualismo, junto com pelo menos 2.500 de carma. Uma vez que você ganhou 250 de ouro com este malho e estaca, entregue-o ao sacerdote e ele o guiará por um caminho de divindade."; }
			else if ( sConversation == "Devon"){ sText = "Saudações, " + sYourName +". Eu sou " + sMyName + " e tenho pescado nestas águas por muitos anos. Não há muita competição nestas águas, pois há apenas alguns de nós que ousaram aventurar-se no Submundo. Tenho visto algumas criaturas estranhas, porém, e ouvi histórias ainda mais estranhas de outros. Construí alguns navios para aventureiros, mesmo sendo principalmente madeira petrificada tão fundo sob a superfície. Posso ter um que interesse você. Diferente dos mares da superfície, lançar um navio aqui é muito mais fácil e, portanto, nunca precisamos realmente contar com um mestre de doca para ajuda. Esta enorme caverna parece invocar uma brisa grande o suficiente para empurrar as velas, então você não deve ter problemas para navegar nas águas. Cuidado com o lago, pois as lendas contam que o Titã da Água supostamente espreita nas profundezas turvas.<br><br>Se você se perder dentro desta caverna, pode usar um sextante mágico para encontrar seu caminho. Pode parecer bobo, pois não há sol aqui, mas tenho sextantes mágicos que podem ver através do teto da caverna quando olhados através dele. Mantenha isso em mente se você estiver procurando por algo também."; }
			else if ( sConversation == "Pets")
			{
				sText = "Saudações, " + sYourName +". Eu sou " + sMyName + " e tenho trabalhado com animais por muitos anos. Domar é um feito que apenas alguns podem realizar. Se você tem animais de estimação, podemos estabulá-los para você, se desejar. Se você tiver uma habilidade de grão-mestre em Acampamento, pode adquirir um poste de amarração e usá-lo em sua casa para estabular seus próprios animais.<br><br>Você só pode ter um número particular de companheiros animais com você, baseado em suas habilidades com animais. Ter uma boa habilidade em Pastoreio pode ajudá-lo a gerenciar mais animais de estimação.<br><br>Se você está procurando por um animal de carga, posso ser capaz de ajudar. Muitos aventureiros parecem adquirir muito tesouro em sua jornada e frequentemente reclamam sobre quanto tiveram que deixar para trás porque simplesmente não podiam carregar. Um cavalo de carga pode ajudar, pois são baratos. Muitos desses animais foram mortos em áreas perigosas, então tenha cuidado e proteja-os de tais coisas. Às vezes somos capazes de criar uma mula de carga saudável, no entanto, e elas são muito mais resistentes do que outros animais de carga. Elas exigem mais controle para trazer com você, mas carregam muito e os monstros parecem deixá-las em paz por algum motivo. Por serem frequentemente procuradas por aventureiros, custam muito mais ouro, mas pode valer a pena. Também ouvi histórias de domadores realmente treinando elefantes e dinossauros para carregar seus bens, mas ainda não vi.";

				if ( MySettings.S_NoMountsInCertainRegions )
				{
					sText = sText + "<br><br>Esteja ciente ao explorar a terra, que há algumas áreas onde você não pode montar em montarias. Estas são áreas como masmorras, cavernas e algumas áreas internas. Se você estiver montado em uma montaria etérea, ela se moverá para sua mochila quando você entrar nessas áreas. Você pode usar a montaria etérea quando sair dessa área. Se você estiver montado em um animal, então o animal fugirá para um lugar seguro quando você entrar nessas áreas. Eles reaparecerão quando você sair da área e você pode continuar montado. Se você não conseguir encontrar seu animal de montaria, por qualquer motivo, verifique os estábulos, pois ele pode ter vagado até lá. Se você tem um animal no qual não pode montar nessas áreas, mas quer que ele o acompanhe de qualquer maneira, então saia da área e desmonte do animal antes de continuar. Se você ficar sem sua montaria por muito tempo, eles eventualmente voltarão para os estábulos, onde o mestre dos estábulos retirará ouro de seu banco a cada semana. Se você não tiver ouro, eles libertarão sua montaria na natureza.";
				}
			}
			else if ( sConversation == "Powerscroll"){ sText = "Salve, " + sYourName + ". Eu sou " + sMyName + " e sou um dos professores do conhecimento superior aqui em Lodoria. Como nós elfos podemos viver por centenas de anos, aprendemos muito mais sobre certas habilidades do que vocês humanos. Embora ensinemos muitos elfos aqui, dentro destas muralhas do castelo, você precisaria embarcar em uma jornada aos santuários do esclarecimento se desejar buscar tais coisas. Se você for capaz de desfazer-se do ouro necessário para nosso conhecimento adquirido, então você deve levar o pergaminho para a terra perdida de Ambrosia. Procure o santuário apropriado do esclarecimento e leia o pergaminho lá.<br><br>Maravilhoso: Até 105 de Habilidade<br>Exaltado: Até 110 de Habilidade<br>Mítico: Até 115 de Habilidade<br>Lendário: Até 120 de Habilidade<br>Poder: Até 125 de Habilidade<br><br>Ambrosia uma vez só podia ser alcançada por navio, mas desde a derrota de Exodus, disse-se que um portal se abriu em sua ilha que supostamente leva até lá. Claro, estes são apenas rumores, mas você pode querer começar por lá. Boa sorte, " + sYourName + "."; }
			else if ( sConversation == "Frankenstein"){ sText = "Salve, " + sYourName + ". Eu sou " + sMyName + " e sou o agente funerário aqui na guilda. Embora meus colegas necromantes estudem os aspectos da morte, eu fico aqui embaixo para estudar o reavivamento da vida. Passei a maior parte da minha vida tentando aprender os segredos de Victor Frankenstein, mas ainda não obtive nenhum de seus diários que possa me levar adiante em meus estudos. Eu tinha um conhecido que estava me ajudando, e ele conseguiu encontrar algum texto sobre o assunto, mas, infelizmente, seu navio se perdeu no mar e ele nunca mais foi visto. É justo, porque parecia que sua descoberta apenas tocava a superfície de cadáveres reanimados.<br><br>Se você conseguir encontrar qualquer um dos diários de Victor Frankenstein, ficaria muito satisfeito em adquiri-los de você. Claro, você é livre para tentar e seguir suas instruções. Talvez você possa ter mais sucesso do que eu se sua habilidade forense for boa o suficiente. Diz-se que esses cadáveres reanimados podem ser combatentes muito poderosos ou escravos muito fortes. Ouvi dizer que o poder de tais criaturas é dependente do poder do cérebro que você pode encontrar para colocar nele. Então, um cérebro de um ogro não seria tão bom quanto um cérebro de um gigante das tempestades. Para obter as partes do corpo que você precisa, você precisará levar um dos diários de Frankenstein com você enquanto usa uma lâmina em cadáveres de gigantes. Quanto melhor sua habilidade forense, melhor chance você terá de obter partes específicas do corpo para construir uma criatura. Você encontrará braços, torsos, pernas, cabeças e cérebros de muitas criaturas diferentes. Estes geralmente não servirão, pois você precisa encontrar membros, cabeças e torsos 'cortados'. Você também precisa de um cérebro 'fresco', pois os outros geralmente estão podres e perderam toda a centelha de vida. Uma vez que você os reúna, precisará de uma bobina de energia como a que tenho aqui no meu laboratório. Seguir as instruções no diário ajudará a animar o cadáver. Você é livre para usar minha bobina de energia, mas também as obtenho de um funileiro local, então você pode comprar uma de mim para colocar em sua casa, se desejar."; }
			else if ( sConversation == "Jester"){ sText = Server.Items.BagOfTricks.JesterSpeech(); }
			else if ( sConversation == "Jedi"){ sText = "Saudações, criança. Não falo com ninguém há bastante tempo. Se você está se perguntando, eu sou um descendente do que é conhecido como Jedi. Há muito tempo, nossa ordem foi abandonada neste mundo. Embora não houvesse meios de partir, tínhamos uma missão de livrar este mundo dos Syth que chegaram durante a mesma época. À medida que os Syth cresciam em número, o mesmo acontecia com os Jedi. Enquanto lidávamos lentamente com a ameaça Syth, muitos de nossa ordem se juntaram à sociedade e se tornaram o que este mundo conhece como sacerdotes. Eles curavam os doentes e forneciam ensinamentos de comportamento virtuoso para todos que quisessem ouvir. Se você é de bom caráter (carma positivo) e tem aptidão para habilidade psíquica (psicologia de 25 ou mais), você é bem-vindo para seguir o caminho do Jedi. Tudo que peço é uma demonstração de comprometimento. Conheço uma família que está em necessidade desesperada e gostaria de deixar 5.000 de ouro para eles começarem uma vida nova. Se você está pronto para o esclarecimento e está disposto a desfazer-se do ouro, então, por favor, entregue-o a mim e eu lhe darei a sabedoria para iniciar sua jornada no caminho do Jedi."; }
			else if (sConversation == "Animal Broker") { sText = "Olá. Posso falar com você um segundo? Tenho um favor a pedir e me pergunto se você poderia me ajudar.<BR><BR>Tenho em minha posse contratos de compradores nas terras por certas bestas. Darei alegremente os contratos a você e guardarei apenas uma pequena comissão para mim se você puder preencher os contratos para mim.<BR>"; }
			else if (sConversation == "Defender of the Realm") { sText = "Olá e bem-vindo, aventureiro!.<BR><BR>Nosso rei me colocou e aos meus no comando da ofensiva contra os muitos inimigos que assombram estas terras, e me deu a autoridade para emitir Votos de Honra para aqueles que se julgarem dignos de embarcar em uma empreitada tão perigosa!<BR><BR>Dei a você um símbolo que considero apropriado para suas habilidades. Vá adiante e traga a ruína aos mestres de nossos inimigos e colecionem seus troféus! Faça isso e complete este voto, e ao trazê-lo de volta para mim, eu o recompensarei por sua bravura!<BR><BR>Simplesmente me pergunte sobre recompensas e eu lhe mostrarei o que posso oferecer!<BR>";}
			else if (sConversation == "Scourge of the Realm") { sText = "Salve e bem-vindo, patife!.<BR><BR>Por muito tempo murchamos à sombra daquele rei patético!<BR><BR>Chega, pois reuniremos os exércitos e tomaremos o que é legitimamente nosso! Proponho um desafio a você:<BR><BR>Vá adiante e mate inimigos poderosos e esculpa um nome para si mesmo a partir dos ossos desta terra amaldiçoada!<BR><BR>Pergunte-me sobre recompensas e eu lhe mostrarei o que posso oferecer!<BR>";}
			return sText;
		}
	}
}

